import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, StatusBar, Image, TouchableOpacity } from 'react-native';

const HomeScreen = () => {
  const [data, setData] = useState({
    silverQuantity: -14500.0,
    goldQuantity: 133.5,
    amountValue: 676000,
    isDaytime: true,
    batteryPercentage: 80,
    networkType: '5G',
    networkStrength: 'High',
    user: {
      name: 'John Doe',
      image: require('./user.png'), // Replace with the actual path to user image
    },
  });

  const [selectedOption, setSelectedOption] = useState('Both');

  useEffect(() => {
    // Fetch dummy data or perform other necessary actions
  }, []);

  const renderStatusBar = () => {
    const { isDaytime, batteryPercentage, networkType, networkStrength } = data;
    const time = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    const icon = isDaytime ? 'ðŸŒž' : 'ðŸŒ™';

    return (
      <View style={styles.statusBar}>
        <Text style={styles.time}>{time}</Text>
        <Text style={styles.icon}>{icon}</Text>
        <Text style={styles.battery}>{batteryPercentage}%</Text>
        <Text style={styles.network}>
          {networkType} {networkStrength}
        </Text>
      </View>
    );
  };

  const renderUserInfo = () => {
    const { name, image } = data.user;

    return (
      <View style={styles.userInfo}>
        <Image source={image} style={styles.userImage} />
        <Text style={styles.userName}>{name}</Text>
      </View>
    );
  };

  const renderSearchBar = () => {
    return (
      <View style={styles.searchBar}>
        <Image source={require('./search.png')} style={styles.searchIcon} />
        <Text style={styles.searchText}>Search Party</Text>
      </View>
    );
  };

  const renderOptions = () => {
    const handleOptionSelect = (option) => {
      setSelectedOption(option);
    };

    return (
      <View style={styles.options}>
        <TouchableOpacity
          style={[styles.optionButton, selectedOption === 'Both' && styles.optionButtonSelected]}
          onPress={() => handleOptionSelect('Both')}
        >
          <Text style={[styles.optionButtonText, selectedOption === 'Both' && styles.optionButtonTextSelected]}>
            Both
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.optionButton, selectedOption === 'Silver' && styles.optionButtonSelected]}
          onPress={() => handleOptionSelect('Silver')}
        >
          <Text style={[styles.optionButtonText, selectedOption === 'Silver' && styles.optionButtonTextSelected]}>
            Silver
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.optionButton, selectedOption === 'Gold' && styles.optionButtonSelected]}
          onPress={() => handleOptionSelect('Gold')}
        >
          <Text style={[styles.optionButtonText, selectedOption === 'Gold' && styles.optionButtonTextSelected]}>
            Gold
          </Text>
        </TouchableOpacity>
      </View>
    );
  };

  const renderContainer = (label, value) => {
    return (
      <View style={styles.container}>
        <Text style={styles.label}>{label}</Text>
        <Text style={styles.value}>{value}</Text>
      </View>
    );
  };

  const { silverQuantity, goldQuantity, amountValue } = data;

  let displayedSilverQuantity = silverQuantity;
  let displayedAmountValue = amountValue;

  if (selectedOption === 'Gold') {
    displayedSilverQuantity = '';
    displayedAmountValue = '';
  } else if (selectedOption === 'Silver') {
    displayedAmountValue = '';
  }

  return (
    <View style={styles.container}>
      <StatusBar barStyle="dark-content" />
      {renderStatusBar()}
      {renderUserInfo()}
      {renderSearchBar()}
      {renderOptions()}
      {renderContainer('Total', amountValue)}
      {renderContainer('Stock', `${displayedSilverQuantity}gms`)}
      {renderContainer('Karigar', `${goldQuantity}gms`)}
      {renderContainer('Supplier', displayedAmountValue)}
      {renderContainer('Bullion', displayedAmountValue)}
      {renderContainer('Customer', displayedAmountValue)}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#fff',
  },
  statusBar: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 16,
  },
  time: {
    fontSize: 16,
  },
  icon: {
    fontSize: 16,
  },
  battery: {
    fontSize: 16,
  },
  network: {
    fontSize: 16,
  },
  userInfo: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16,
  },
  userImage: {
    width: 40,
    height: 40,
    borderRadius: 20,
    marginRight: 8,
  },
  userName: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  searchBar: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16,
  },
  searchIcon: {
    width: 20,
    height: 20,
    marginRight: 8,
  },
  searchText: {
    fontSize: 16,
  },
  options: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 16,
  },
  optionButton: {
    flex: 1,
    alignItems: 'center',
    paddingVertical: 8,
    borderRadius: 8,
    backgroundColor: '#E5E5E5',
  },
  optionButtonSelected: {
    backgroundColor: '#555555',
  },
  optionButtonText: {
    fontSize: 16,
    color: '#555555',
  },
  optionButtonTextSelected: {
    color: '#FFFFFF',
  },
  container: {
    marginBottom: 16,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  value: {
    fontSize: 16,
  },
});

export default HomeScreen;
