import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

const HomeScreen = () => {
  const currentTime = new Date().getHours();
  const isDaytime = currentTime >= 6 && currentTime < 18;

  return (
    <View style={styles.container}>
      <View style={styles.notificationBar}>
        <View style={styles.timeContainer}>
          <Text style={styles.time}>{getCurrentTime()}</Text>
          <Icon
            name={isDaytime ? 'sun-o' : 'moon-o'}
            style={styles.dayNightIcon}
          />
        </View>
        <View style={styles.networkContainer}>
          <Text style={styles.networkText}>5G</Text>
          <Icon name="signal" style={styles.networkIcon} />
          <Text style={styles.batteryText}>80%</Text>
          <Icon name="battery-full" style={styles.batteryIcon} />
        </View>
      </View>
      <View style={styles.userInfoContainer}>
        <Image
          source={require('./path/to/userImage.png')}
          style={styles.userImage}
        />
        <Text style={styles.userName}>John Doe</Text>
      </View>
      <View style={styles.searchContainer}>
        <Icon name="search" style={styles.searchIcon} />
        <Text style={styles.searchText}>Search Party</Text>
      </View>
    </View>
  );
};

const getCurrentTime = () => {
  const date = new Date();
  const hours = date.getHours();
  const minutes = date.getMinutes();
  return `${padZero(hours)}:${padZero(minutes)}`;
};

const padZero = (value) => {
  return value < 10 ? `0${value}` : `${value}`;
};

const styles = StyleSheet.create({
  // ...existing styles
});

export default HomeScreen;
