import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, StatusBar, Image } from 'react-native';

const HomeScreen = () => {
  const [data, setData] = useState({
    silverQuantity: -14500.0,
    goldQuantity: 133.5,
    amountValue: 676000,
    isDaytime: true,
    batteryPercentage: 80,
    networkType: '5G',
    networkStrength: 'High',
    user: {
      name: 'John Doe',
      image: require('./user.png'), // Replace with the actual path to user image
    },
  });

  useEffect(() => {
    // Fetch dummy data or perform other necessary actions
  }, []);

  const renderStatusBar = () => {
    const { isDaytime, batteryPercentage, networkType, networkStrength } = data;
    const time = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    const icon = isDaytime ? 'ðŸŒž' : 'ðŸŒ™';

    return (
      <View style={styles.statusBar}>
        <Text style={styles.time}>{time}</Text>
        <Text style={styles.icon}>{icon}</Text>
        <Text style={styles.battery}>{batteryPercentage}%</Text>
        <Text style={styles.network}>
          {networkType} {networkStrength}
        </Text>
      </View>
    );
  };

  const renderUserInfo = () => {
    const { name, image } = data.user;

    return (
      <View style={styles.userInfo}>
        <Image source={image} style={styles.userImage} />
        <Text style={styles.userName}>{name}</Text>
      </View>
    );
  };

  const renderSearchBar = () => {
    return (
      <View style={styles.searchBar}>
        <Image source={require('./search.png')} style={styles.searchIcon} />
        <Text style={styles.searchText}>Search Party</Text>
      </View>
    );
  };

  const renderOptions = () => {
    return (
      <View style={styles.options}>
        <Text style={styles.option}>Both</Text>
        <Text style={styles.option}>Silver</Text>
        <Text style={styles.option}>Gold</Text>
      </View>
    );
  };

  const renderContainer = (label, value) => {
    return (
      <View style={styles.container}>
        <Text style={styles.label}>{label}</Text>
        <Text style={styles.value}>{value}</Text>
      </View>
    );
  };

  const { silverQuantity, goldQuantity, amountValue } = data;

  return (
    <View style={styles.container}>
      <StatusBar barStyle="dark-content" />
      {renderStatusBar()}
      {renderUserInfo()}
      {renderSearchBar()}
      {renderOptions()}
      {renderContainer('Total', amountValue)}
      {renderContainer('Stock', `${silverQuantity}gms`)}
      {renderContainer('Karigar', `${goldQuantity}gms`)}
      {renderContainer('Supplier', amountValue)}
      {renderContainer('Bullion', amountValue)}
      {renderContainer('Customer', amountValue)}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#fff',
  },
  statusBar: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 16,
  },
  time: {
    fontSize: 16,
  },
  icon: {
    fontSize: 16,
  },
  battery: {
    fontSize: 16,
  },
  network: {
    fontSize: 16,
  },
  userInfo: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16,
  },
  userImage: {
    width: 40,
    height: 40,
    borderRadius: 20,
    marginRight: 8,
  },
  userName: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  searchBar: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16,
  },
  searchIcon: {
    width: 20,
    height: 20,
    marginRight: 8,
  },
  searchText: {
    fontSize: 16,
  },
  options: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 16,
  },
  option: {
    fontSize: 16,
  },
  container: {
    marginBottom: 16,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  value: {
    fontSize: 16,
  },
});

export default HomeScreen;
